import React from 'react';
import Dropzone from 'react-dropzone';
import { Grid, Container, AppBar, Tabs, Tab, Box, Button} from '@material-ui/core';
// import TabPanel from '@material-ui/lab/TabPanel';
// import "./App.css";

class App extends React.Component {
  state = {
    data: null
  };

  componentDidMount() {
    this.callServer()
    .then(res => this.setState({data: res.express}))
    .catch(err => console.log(err));
  }

  callServer = async () => {
    const response = await fetch('/express-backend');
    const body = await response.json()

    if(response.status !== 200) {
      throw Error(body.message)
    }
    return body
  };

  render() {
    let value = undefined
    let handleChange = undefined
    return (
      <Grid container className="App" direction="row" alignItems="stretch">
        <AppBar position="static">
          <Tabs value={value} onChange={handleChange} aria-label="simple tabs example">
            <Tab label="Item One" />
            <Tab label="Item Two" />
            <Tab label="Item Three" />
          </Tabs>
        </AppBar>
        <p>{this.state.data}</p>
        <Dropzone onDrop={acceptedFiles => console.log(acceptedFiles)}>
          {({getRootProps, getInputProps}) => (
            <section>
              <div {...getRootProps()}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here, or click to select files</p>
              </div>
            </section>
          )}
        </Dropzone>
        <Container maxWidth='xl'>
          <form style={{padding: "10px"}}>
            <input type="text"></input>
            <input type="submit"></input>
          </form>
          {/* <Box component="span" m={12}>
            <Button variant="contained" color="primary">Press me</Button>
          </Box> */}
        </Container>
      </Grid>
    );
  }
}

export default App;
