import React from 'react';
import { withStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextDetection from './Containers/TextDetection/TextDetection'
import TextTable from './Components/TextTable/TextTable'

const styles = (theme) => ({
  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }
})


class App extends React.Component {
  constructor() {
    super()
    this.state = {
      text: []
    };
    this.receivedText = this.receivedText.bind(this)
  }

  receivedText = async (detectedText) => {
    let update = await this.setState({text: detectedText}, function() {
      console.log(detectedText)
    })
    console.log(update)
  }

  render() {
    const { classes } = this.props
    return (
      <div className={classes.root} spacing={2}>
        <Grid item xs={12} sm={4}>
          <TextDetection textToParent={this.receivedText}/>
        </Grid>
        <Grid item xs={12} sm={8}>
          <TextTable segments={this.state.text}/>
        </Grid>
      </div>
    );
  }
}

export default withStyles(styles, {withTheme: true})(App);
