import React from 'react';
import Dropzone from 'react-dropzone';
import { Grid, Container, AppBar, Tabs, Tab, Box, Button} from '@material-ui/core';
// import TabPanel from '@material-ui/lab/TabPanel';
import "./App.css";

class App extends React.Component {
  state = {
    data: null
  };

  componentDidMount() {
    // this.callServer()
    // .then(res => this.setState({data: res.express}))
    // .catch(err => console.log(err));
    this.getText()
    .then(res => this.setState(res.description))
    .catch(err => console.log(err));
  };

  // callServer = async () => {
  //   const response = await fetch('/express_backend');
  //   const body = await response.json()
  //   console.log(body)
  //   if(response.status !== 200) {
  //     throw Error(body.message)
  //   }
  //   return body
  // };

  getText = async () => {
    const response  = await fetch('/read_text');
    const body = await response.json();
    console.log(body)
    if(response.status !== 200) {
      throw Error(body.message)
    }
    return body
  };

  render() {
    let value = undefined
    let handleChange = undefined
    return (
      <Grid container className="App" direction="row" alignItems="stretch">
        <AppBar id="appBar" position="static">
          {/* <Tabs value={value} onChange={handleChange} aria-label="simple tabs example">
            <Tab label="Item One" />
            <Tab label="Item Two" />
            <Tab label="Item Three" />
          </Tabs> */}
        </AppBar>
        <p>{this.state.data}</p>
        <Container id="dropzoneContainer">
          <Dropzone id="dropzone" onDrop={acceptedFiles => console.log(acceptedFiles)}>
            {({getRootProps, getInputProps}) => (
              <section>
                <div {...getRootProps()}>
                  <input {...getInputProps()} />
                  <p>Drag 'n' drop some files here, or click to select files</p>
                </div>
              </section>
            )}
          </Dropzone>
        </Container>
        <Container id="buttonContainer">
          <button id="googleVision" type="button">Google Vision</button>
          <button id="tesseractJS" type="button">Tesseract.js</button>
        </Container>
        <Container id="formContainer" maxWidth='xl'>
          <form style={{padding: "10px"}}>
            <input type="text"></input>
            <input type="submit"></input>
          </form>
          {/* <Box component="span" m={12}>
            <Button variant="contained" color="primary">Press me</Button>
          </Box> */}
        </Container>
      </Grid>
    );
  }
}

export default App;
