import React, { useState }from 'react';
import { makeStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid';
import Header from './Containers/Header/Header'
import TextDetection from './Containers/TextDetection/TextDetection'
import TextTable from './Components/TextTable/TextTable'
import { dark } from '@material-ui/core/styles/createPalette';

const useStyles = makeStyles((theme) => ({
  palette: {
    type: dark,
  },
  root: {
    flexGrow: 1,
    height: '100vh'
  },
  mainArea:{
    marginTop: 20,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  },
  tableGrid:{
    justifyContent: 'center'
  }
}))

export default function App() {
  const [text, setText] = useState([])

  const classes = useStyles()

  function clearText() {
    setText("")
  }

  return (
    <div className={classes.root}>
      <Header />
      <Grid container className={classes.mainArea}>
        <Grid item xs={12} sm={4} m={4}>
          <TextDetection textToParent={setText} clearText={clearText}/>
        </Grid>
        <Grid item className={classes.tableGrid} xs={12} sm={8} m={6}>
          <TextTable segments={text}/>
      </Grid>
      </Grid>
    </div>
  );
}
