import React from 'react';
import Dropzone from 'react-dropzone';
import { Grid, Container, AppBar } from '@material-ui/core';
import "./App.css";

class App extends React.Component {
  state = {
    data: null,
    acceptedImage: ""
  };

  getText = async () => {
    const response  = await fetch('/read_text');
    const body = await response.json();
    console.log(body)
    if(response.status !== 200) {
      throw Error(body.message)
    }
    return body
  };

  postText = async () => {
    const response = await fetch('/submit_text', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        image: this.state.acceptedImage
      })
    })
  }

  printText() {
    this.getText()
    .then(res => this.setState({data: res}))
    .catch(err => console.log(err));
  };

  render() {
    return (
      <Grid container className="App" direction="row" alignItems="stretch">
        <AppBar id="appBar" position="static">
        </AppBar>
        <Container id="dropzoneContainer">
          <Dropzone id="dropzone" onDrop={acceptedFiles => this.setState(() => ({acceptedImage: acceptedFiles}), console.log(this.state.acceptedImage))}>
            {({getRootProps, getInputProps}) => (
              <section>
                <div {...getRootProps()}>
                  <input {...getInputProps()} />
                  <p>Drag 'n' drop some files here, or click to select files</p>
                </div>
              </section>
            )}
          </Dropzone>

        <p>{this.state.data}</p>
        </Container>
        <Container id="buttonContainer">
          <button id="googleVision" type="button" onClick={() => {this.printText()}}>Google Vision</button>
          <button id="tesseractJS" type="button">Tesseract.js</button>
        </Container>
        <Container id="formContainer" maxWidth='xl'>
          <form style={{padding: "10px"}}>
            <input type="text"></input>
            <input type="submit"></input>
          </form>
        </Container>
      </Grid>
    );
  }
}

export default App;
