import React, { useState, useEffect, useRef, createRef} from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Checkbox from '@material-ui/core/Checkbox';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField'

import {Select, MenuItem } from '@material-ui/core'


const useStyles = makeStyles(() => ({
    container: {
        marginLeft: 10,
        marginRight: 10,
        maxHeight: 'calc(100vh - 150px)',
        height: '100%',
    },
}));

const StyledTableCell = withStyles((theme) => ({
    head: {
    //   backgroundColor: theme.palette.common.black,
    //   color: theme.palette.common.white,
    },
    body: {
      fontSize: 14,
    },

  }))(TableCell);

const StyledTableRow = withStyles((theme) => ({
root: {
    '&:nth-ot-type(odd)': {
    backgroundColor: theme.palette.action.hover,
    },
},
}))(TableRow);


export default function TextTable({segments}) {
    const defaultCategories = [{value:'Invoice Number'}, {value:'Quantity'}, {value:'Price'}]

    const [categories, updateCategories] = useState(defaultCategories)

    // const catRefs = useRef([])

    const [currentCategory, setSelected] = useState('')

    const handleChange = (event) => {
        setSelected(event.target.value);
        catRefs.current[1] = segments[0]
        // updateCategories(categories.filter(category => category.value !== event.target.value)) //this line is commented out because it removes the text from the selected MenuItem
    }

    const segmentsLength = segments.length;

    // if(catRefs.current.length !== segmentsLength) {
    //     catRefs.current = Array(segmentsLength).fill().map((_, i) => catRefs.current[i])
    //     console.log(catRefs.current)
    // }

    const [catRefs, setCatRefs] = useState([]);

    useEffect(() => {
        setCatRefs(catRefs => (
            Array(segmentsLength).fill().map((_, i) => catRefs[i] || createRef())
        ));
    }, [segmentsLength])

    const classes = useStyles()

    return (
        <TableContainer component={Paper} className={classes.container}>
            <Table stickyHeader aria-label="sticky table">
                <TableHead>
                <TableRow>
                    <StyledTableCell></StyledTableCell>
                    <StyledTableCell>Category</StyledTableCell>
                    <StyledTableCell>Transcribed Text</StyledTableCell>
                </TableRow>
                </TableHead>
                <TableBody>
                    {
                        segments ?
                        segments.map((segment, i) => {
                            if (segment !== "") {
                                return (
                                <StyledTableRow key={i}>
                                    <TextField
                                        ref={catRefs[i]} 
                                        // disabled
                                    >
                                    {/* {catRefs.current} */}
                                    </TextField>
                                    <Select
                                        key={i}
                                        onChange={handleChange}
                                    >
                                        {categories.map((category, i) => {
                                            return (
                                                <MenuItem 
                                                key={i}
                                                value={category.value}
                                                >
                                                    {category.value}
                                                </MenuItem>
                                            )
                                        })}
                                    </Select>
                                    <StyledTableCell>{i+1}</StyledTableCell>
                                    <StyledTableCell>{segment}</StyledTableCell>
                                </StyledTableRow>
                                )
                            } else {
                                return null
                            }
                        }) 
                        :
                        <StyledTableRow>
                            <Checkbox></Checkbox>
                            <StyledTableCell></StyledTableCell>
                            <StyledTableCell></StyledTableCell>
                        </StyledTableRow>
                    }
                </TableBody>
            </Table>
        </TableContainer>
    )
}
