import React, { useState }from 'react';
import { makeStyles } from '@material-ui/core/styles'
import Grid from '@material-ui/core/Grid';
import Header from './Containers/Header/Header';
import TextDetection from './Containers/TextDetection/TextDetection';
import TextTable from './Components/TextTable/TextTable';

import { createMuiTheme , ThemeProvider } from '@material-ui/core/styles'
import {
  orange,
  lightBlue,
  deepPurple,
  deepOrange
} from "@material-ui/core/colors";

const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    // flexGrow: 1,
    // height: '100vh'
  },
  mainArea:{
    marginTop: 20,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: 'center',
    color: theme.palette.text.secondary,
  }
}))

export default function App() {
  const [darkState, setDarkState ] = useState(false);
  const [text, setText] = useState([]);

  const classes = useStyles()

  const palletType = darkState ? "dark" : "light";
  const mainPrimaryColor = darkState ? orange[500] : lightBlue[500];
  const mainSecondaryColor = darkState ? deepOrange[900] : deepPurple[500];

  const darkTheme = createMuiTheme({
    palette: {
      type: palletType,
    },
    primary: {
      main: mainPrimaryColor
    },
    secondary: {
      main: mainSecondaryColor
    },
  });

  const handleThemeChange =() => {
    setDarkState(!darkState)
  };

  function clearText() {
    setText("")
  }

  return (
    <ThemeProvider theme={darkTheme}>
      <div className={classes.root}>
        <Grid>
          <Header darkState={darkState} handleDarkMode={handleThemeChange}/>

        </Grid>
        <Grid container className={classes.mainArea}>
          <Grid item xs={12} sm={4}>
            <TextDetection textToParent={setText} clearText={clearText}/>
          </Grid>
          <Grid item xs={12} sm={8}>
            <TextTable segments={text}/>
        </Grid>
        </Grid>
      </div>
    </ThemeProvider>
  );
}
