import React from 'react';
import { makeStyles } from '@material-ui/core/styles'
import { Grid, Container, Card, Button } from '@material-ui/core';
import Dropzone from '../../Components/Dropzone/Dropzone'

const useStyles = makeStyles(() => ({
  dropzoneCard: {
    marginBottom: 40,
  },
  buttonArea: {
    display: 'flex',
    justifyContent: 'center'
  }
}));

function TextDetection({ keyDataToParent, clearText }) {
  const classes = useStyles()

  const uploadImage = async (image) => {
    var formData = new FormData();
    formData.append('photo', image[0])
    const formResponse = await fetch('/getFormData', {
      method: "post",
      body: formData
    })
    const formResponseData = await formResponse.json();

    // const tableResponse = await fetch('/getTableData', {
    //   method: "post",
    //   body: formData
    // })

    // const tableResponseData = await tableResponse.json();
    // console.log(tableResponseData)
    console.log(formResponseData)
    // keyDataToParent(Object.entries(formResponseData))
    formResponseData.purchaseOrders.forEach((number) => searchBP(number))
  }

  const searchBP = async (orderNumber) => {
    const findOrder = await fetch('/queryBp', {
      method: "POST",
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(orderNumber)
    });
    const testComplete = await findOrder.json()
    console.log(testComplete)
  }

  // const testComplete = await testBP().json()
  // console.log(testComplete)

  return (
    <Grid>
      <Container>
        <Card variant="outlined" className={classes.dropzoneCard}>
          <Dropzone uploadImage = {uploadImage}/>
        </Card>
      </Container>
      <Container className={classes.buttonArea}>
        <Button variant="outlined" type="button" onClick={() => clearText()}>Clear text</Button>
      </Container>
      <Container className={classes.buttonArea}>
        <Button variant="outlined" type="button" onClick={() => searchBP({"orderId": "101603"})}>Query BP</Button>
      </Container>
    </Grid>
  );
}

export default TextDetection;
